Ext.define('foodprint.controller.ErpPurchaseSheetController', {
    extend: 'Ext.app.Controller',

    mixins: {
        commonController: 'foodprint.controller.CommonController'
    },

    models: [
        'ErpPurchaseSheet'
    ],
    stores: [
        'ErpPurchaseSheetStore'
    ],
    views: [
        'ErpPurchaseSheetView'
    ],

    refs: [
        {
            ref: 'mainGrid',
            selector: 'erppurchasesheetview #grid'
        },
        {
            ref: 'mainForm',
            selector: 'erppurchasesheetview #form'
        },
        {
            ref: 'detailGrid',
            selector: 'erppurchasesheetview #detailGrid'
        },
        {
            ref: 'detailForm',
            selector: 'erppurchasesheetview #detailForm'
        }
    ],

    init: function(application) {
        this.control({
            'erppurchasesheetview #index commonindextoolbar commoncreatebtn':{
                click:this.doCreateAndIndexDetail
            },
            'erppurchasesheetview #index commonindextoolbar commonshowbtn':{
                click:this.doShowPurchaseSheet
            },
            'erppurchasesheetview #show commonshowtoolbar commondeletebtn':{
                click:this.doDelete
            },
            'erppurchasesheetview #show commonshowtoolbar commonsavebtn':{
                click:this.doSave
            },
            'erppurchasesheetview #show commonshowtoolbar commoncancelbtn':{
                click:this.doCancel
            },
            'erppurchasesheetview #grid':{
                select: this.enableShowBtn,
                deselect: this.disableShowBtn,
                itemdblclick: this.doShowPurchaseSheet
            },
            'erppurchasesheetview #show commonindextoolbar commoncreatebtn':{
                click:this.doCreateDetail
            },
            'erppurchasesheetview #show commonindextoolbar commonshowbtn':{
                click:this.doShowPurchaseSheetDet
            },
            'erppurchasesheetview #showDetail commonshowtoolbar commondeletebtn':{
                click:this.doDeleteDetail
            },
            'erppurchasesheetview #showDetail commonshowtoolbar commonsavebtn':{
                click:this.doSaveDetail
            },
            'erppurchasesheetview #showDetail commonshowtoolbar commoncancelbtn':{
                click:this.doCancelDetail
            },
            'erppurchasesheetview #detailGrid':{
                select: this.enableDetailShowBtn,
                deselect: this.disableDetailShowBtn,
                itemdblclick: this.doShowPurchaseSheetDet
            }

        });


        this.domainName = 'foodpaint';
        this.foodpaintController = 'purchaseSheet';
        this.foodpaintDetController = 'purchaseSheetDet';
        this.masterKey='purchaseSheet.id';
    },

    doShowPurchaseSheet: function() {
        this.doShowAndIndexDetail(function(success,form,action){
            //由於store設定load第1-50筆
            //導致doShow時若資料屬於第50筆之後無法正常顯示
            //在此使combo重新load store
            var spcombo=form.findField('supplier.id');
            Utilities.comboReload(spcombo,action.result.data['supplier.id'],action.result.data['supplier.name']);

        });
    },

    doShowPurchaseSheetDet: function() {

        this.doShowDetail(function(success,form,action){
            //由於store設定load第1-50筆
            //導致doShow時若資料屬於第50筆之後無法正常顯示
            //在此使combo重新load store
            var itemcombo=form.findField('item.id');
            Utilities.comboReload(itemcombo,action.result.data['item.id'],action.result.data['item.name']);

            var whcombo=form.findField('warehouse.id');
            Utilities.comboReload(whcombo,action.result.data['warehouse.id'],action.result.data['warehouse.name']);

            //warehouseLocation combo需指定warehouse id才可load
            var wlcombo=form.findField('warehouseLocation.id');
            Utilities.compositionComboReload(wlcombo, 'warehouse.id', action.result.data['warehouse.id'],action.result.data['warehouseLocation.id']);
        });
    }

});
