Ext.define('foodprint.controller.ErpStockInSheetController', {
    extend: 'Ext.app.Controller',

    mixins: {
        commonController: 'foodprint.controller.CommonController'
    },

    models: [
        'ErpStockInSheet',
        'ErpStockInSheetDet',
        'Workstation',
        'Warehouse',
        'WarehouseLocation',
        'ErpManufactureOrder'
    ],
    stores: [
        'ErpStockInSheetStore',
        'ErpStockInSheetDetStore',
        'WorkstationStore',
        'WarehouseStore',
        'WarehouseLocationStore',
        'ErpManufactureOrderStore'
    ],
    views: [
        'ErpStockInSheetView'
    ],

    refs: [
        {
            ref: 'mainGrid',
            selector: 'erpstockinsheetview #grid'
        },
        {
            ref: 'mainForm',
            selector: 'erpstockinsheetview #form'
        },
        {
            ref: 'detailGrid',
            selector: 'erpstockinsheetview #detailGrid'
        },
        {
            ref: 'detailForm',
            selector: 'erpstockinsheetview #detailForm'
        }
    ],

    init: function(application) {
        this.control({
            'erpstockinsheetview #index commonindextoolbar commoncreatebtn':{
                click:this.doCreateAndIndexDetail
            },
            'erpstockinsheetview #index commonindextoolbar commonshowbtn':{
                click:this.doShowStockInSheet
            },
            'erpstockinsheetview #show commonshowtoolbar commondeletebtn':{
                click:this.doDelete
            },
            'erpstockinsheetview #show commonshowtoolbar commonsavebtn':{
                click:this.doSave
            },
            'erpstockinsheetview #show commonshowtoolbar commoncancelbtn':{
                click:this.doCancel
            },
            'erpstockinsheetview #show commonshowtoolbar commonprintbtn':{
                click:this.doPrint
            },
            'erpstockinsheetview #grid':{
                select: this.enableShowBtn,
                deselect: this.disableShowBtn,
                itemdblclick: this.doShowStockInSheet
            },
            'erpstockinsheetview #show commonindextoolbar commoncreatebtn':{
                click:this.doCreateDetail
            },
            'erpstockinsheetview #show commonindextoolbar commonshowbtn':{
                click:this.doShowStockInSheetDet
            },
            'erpstockinsheetview #showDetail commonshowtoolbar commondeletebtn':{
                click:this.doDeleteDetail
            },
            'erpstockinsheetview #showDetail commonshowtoolbar commonsavebtn':{
                click:this.doSaveDetail
            },
            'erpstockinsheetview #showDetail commonshowtoolbar commoncancelbtn':{
                click:this.doCancelDetail
            },
            'erpstockinsheetview #detailGrid':{
                select: this.enableDetailShowBtn,
                deselect: this.disableDetailShowBtn,
                itemdblclick: this.doShowStockInSheetDet
            },
            'erpstockinsheetview #showDetail commonselectbtn':{
                click:this.activeManufactureOrderIndex
            },
            'erpstockinsheetview #manufactureOrderIndex erpmanufactureordergrid':{
                itemdblclick: this.doSelectManufactureOrder
            }

        });


        this.domainName = 'foodpaint';
        this.foodpaintController = 'stockInSheet';
        this.foodpaintDetController = 'stockInSheetDet';
        this.masterKey='stockInSheet.id';
    },

    doSelectManufactureOrder: function(obj, record, index, eOpts) {
        this.getDetailForm().getForm().setValues({

            'manufactureOrder.id':record.data['id'],
            'manufactureOrder.typeName':record.data['typeName'],
            'manufactureOrder.name':record.data['name'],
            'item.id':record.data['item.id'],
            'item.name':record.data['item.name'],
            'item.title':record.data['item.title'],
            'batch.name':record.data['batch.name'],
            'qty':record.data['qty']
        });
        this.activeDetailEditor();
    },

    doShowStockInSheet: function() {
        this.doShowAndIndexDetail(function(success,form,action){
            //由於store設定load第1-50筆
            //導致doShow時若資料屬於第50筆之後無法正常顯示
            //在此使combo重新load store
            var wscombo=form.findField('workstation.id');
            Utilities.comboReload(wscombo,action.result.data['workstation.id'],action.result.data['workstation.name']);
        });
    },

    doShowStockInSheetDet: function() {
        this.doShowDetail(function(success,form,action){
            //由於store設定load第1-50筆
            //導致doShow時若資料屬於第50筆之後無法正常顯示
            //在此使combo重新load store
            var whcombo=form.findField('warehouse.id');
            Utilities.comboReload(whcombo,action.result.data['warehouse.id'],action.result.data['warehouse.name']);

            //warehouseLocation combo需指定warehouse id才可load
            var wlcombo=form.findField('warehouseLocation.id');
            Utilities.compositionComboReload(wlcombo, 'warehouse.id', action.result.data['warehouse.id'],action.result.data['warehouseLocation.id']);
        });
    }

});
