Ext.define('foodprint.view.CommonTraceToolbar', {
    extend: 'Ext.toolbar.Toolbar',
    alias: 'widget.commontracetoolbar',

    requires: [
        'foodprint.view.CommonBatchCombo'
    ],

    itemId: 'commonTraceToolbar',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                me.processCommonExpandallBtn({
                    xtype: 'button',
                    disabled: true,
                    itemId: 'commonExpandallBtn',
                    text: 'ExpandAll'
                }),
                me.processCommonCollapseallBtn({
                    xtype: 'button',
                    disabled: true,
                    itemId: 'commonCollapseallBtn',
                    text: 'CollapseAll'
                }),
                {
                    xtype: 'commonbatchcombo'
                }
            ]
        });

        me.callParent(arguments);
    },

    processCommonExpandallBtn: function(config) {
        config.text=Utilities.getMsg('common.expandBtn.label');

        return config;
    },

    processCommonCollapseallBtn: function(config) {
        config.text=Utilities.getMsg('common.collapseallBtn.label');

        return config;
    }

});