Ext.define('foodprint.view.MainViewport', {
    extend: 'Ext.container.Viewport',
    alias: 'widget.mainviewport',

    requires: [
        'foodprint.view.LoginContainer',
        'foodprint.view.UserForm'
    ],

    id: 'mainVP',
    layout: {
        type: 'card'
    },

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'logincontainer'
                },
                me.processShow({
                    xtype: 'panel',
                    tbar: {
                        xtype: 'commonshowtoolbar'
                    },
                    itemId: 'show',
                    layout: {
                        align: 'stretch',
                        type: 'vbox'
                    },
                    items: [
                        {
                            xtype: 'userform',
                            flex: 1
                        }
                    ]
                })
            ],
            listeners: {
                afterrender: {
                    fn: me.onMainVPAfterRender,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    processShow: function(config) {
        return Utilities.processConfigBundle(config, 'user');
    },

    onMainVPAfterRender: function(component, eOpts) {
        var mask = Ext.get('loading-mask');
        // Destroy the masks
        if(mask)
        mask.fadeOut({callback: function(){ mask.destroy(); }});

        Ext.getBody().removeCls( 'splash' );
    }

});